# Initialize arrays for Homer (character) and the overall image
homer = Array.new(22) { Array.new(40, ' ')}
image = Array.new(24) { Array.new(42, ' ')}

# Populate the Homer array with characters based on the character codes
(0..21).each do |row|
  (0..39).each do |column| 
    homer[row][column] = data[row][column].chr
  end
end

# Fill the first and last rows of the image with asterisks
image[0].fill("*")
image[23].fill("*")

# Fill the first and last columns of the image with asterisks
(0..23).each do |row|
  (0..41).each do |column| 
    image[row][column] = "*" if column == 0 || column == 41
  end
end

# Copy the characters from the Homer array to the corresponding positions in the image array
(1..22).each do |row|
  (1..40).each do |column| 
    image[row][column] = homer[row-1][column-1]
  end
end

# Print the final image
image.each do |row|
  row.each do |c|
    print c
  end
  puts
end
